import React, { useState } from 'react';
import { Client, ConnectConfig } from 'ssh2';

const config: ConnectConfig = {
  host: '100.121.193.21',
  port: 22,
  username: 'myuser',
  password: 'mypassword'
};

const SshButton = () => {
  const [connected, setConnected] = useState(false);

  const connectToSsh = () => {
    const conn = new Client();
    conn.on('ready', () => {
      console.log('SSH connection successful');
      setConnected(true);
    });
    conn.on('error', (err) => {
      console.error('Error connecting:', err);
    });
    conn.connect(config);
  };

  return (
    <div>
      {connected ? (
        <p>SSH connection successful!</p>
      ) : (
        <button onClick={connectToSsh}>Connect to SSH</button>
      )}
    </div>
  );
};

export default SshButton;
