import React, { useState } from 'react';
import { Client, ConnectConfig } from 'ssh2';

interface SSHConnectionProps extends ConnectConfig {
  children: (client: Client) => JSX.Element;
}

function SSHConnection(props: SSHConnectionProps) {
  const { host, port, username, password, children } = props;
  const [client, setClient] = useState<Client | null>(null);

  function connect() {
    const conn = new Client();
    conn.on('ready', () => setClient(conn));
    conn.connect({ host, port, username, password });
  }

  if (!client) {
    connect();
    return null; // or a loading indicator
  }

  return children(client);
}

export default SSHConnection;
