import React, { Component } from 'react';
import './Dropdown.css'; // Import the CSS file with styles

interface DropdownProps {
  buttonText: string;
}

interface DropdownState {
  isDropdownVisible: boolean;
}

class Dropdown extends Component<DropdownProps, DropdownState> {
  constructor(props: DropdownProps) {
    super(props);
    this.state = {
      isDropdownVisible: false,
    };
  }

  toggleDropdown = () => {
    this.setState((prevState) => ({
      isDropdownVisible: !prevState.isDropdownVisible,
    }));
  };

  render() {
    return (
      <div className={`dropdown ${this.state.isDropdownVisible ? 'show' : ''}`}>
        <button className="dropdown-button" onClick={this.toggleDropdown}>
          {this.props.buttonText}
        </button>
        <div className="dropdown-content">
          <button className="dropdown-button">Button 1</button>
          <a href="#" className="dropdown-item">Item 1</a>
          <a href="#" className="dropdown-item">Item 2</a>
          <a href="#" className="dropdown-item">Item 3</a>
        </div>
      </div>
    );
  }
}

export default Dropdown;
