import React, { useState } from 'react';
import { Client } from 'ssh2';

const MyComponent = () => {
  const [sftpOutput, setSftpOutput] = useState('');

  const handleSftpConnect = async () => {
    const sftp = new Client();
    await sftp.connect({
      host: '100.121.193.21',
      port: 22,
      username: 'administrator',
      password: 'clearpath',
    });
    setSftpOutput('SFTP connection established');

    const fileList = await sftp.list('/');
    setSftpOutput(`File list: ${JSON.stringify(fileList)}`);
    sftp.end();
  };

  return (
    <div>
      <button onClick={handleSftpConnect}>Connect to SFTP</button>
      <div>{sftpOutput}</div>
    </div>
  );
};

export default MyComponent;

