import { Fragment, FunctionComponent } from "react";
import SSH from "node-ssh";

const SshConnection: FunctionComponent = () => {
  const [directoryContent, setDirectoryContent] = useState<string[]>([]);
  const [sshConnection, setSshConnection] = useState<NodeSSH | null>(null);

  useEffect(() => {
    const ssh = new NodeSSH();
    const remoteHost = 'example.com'; // Replace with the hostname or IP address of your remote server
    const username = 'myusername'; // Replace with your SSH username
    const password = 'mypassword'; // Replace with your SSH password

    async function connectToRemoteServer() {
      try {
        // Connect to the remote server using username and password
        await ssh.connect({
          host: remoteHost,
          username,
          password
        });
        setSshConnection(ssh);
      } catch (error) {
        console.error('Error connecting to remote server:', error);
      }
    }

    connectToRemoteServer();

    // Disconnect from the remote server when the component unmounts
    return () => {
      if (sshConnection) {
        sshConnection.dispose();
      }
    };
  }, []);

  useEffect(() => {
    async function getDirectoryContent() {
      if (!sshConnection) {
        return;
      }

      // Execute the "ls" command on the remote server to get the content of the home directory
      const { stdout } = await sshConnection.execCommand('ls');
      setDirectoryContent(stdout.trim().split('\n'));
    }

    getDirectoryContent();
  }, [sshConnection]);

  if (!sshConnection) {
    return <div>Connecting to remote server...</div>;
  }

  return (
    <div>
      <p>Contents of home directory:</p>
      <ul>
        {directoryContent.map((item) => (
          <li key={item}>{item}</li>
        ))}
      </ul>
    </div>
  );
};


export default SshConnection;
