import { FunctionComponent } from "react"
import ReactPlayer from "react-player"
import Dropdown from "../../Molecules/knop"


export interface ItemProps {
	text: string;
	videoUrl: string;
	imgSrc: string;
	textExplaination: string;
}


const GridItem: FunctionComponent<ItemProps> = ({
	text,
	videoUrl,
	imgSrc,
	textExplaination
}) => {
	return (
		<div className="grid-item">
			<div className="video-bottom-section">
				<p>{text}</p>
			</div>


			<div className="grid-item__content">
				<img src={imgSrc} />
				<Dropdown buttonText="V">

					<div className="dropdown-item__text">
						<p>{textExplaination}</p>
					</div>
					<p style ="text-align:center; ">uitleg video</p>
					<ReactPlayer className="dropdown-video" height="100%" width="auto" borderRadius="0.25rem" url={videoUrl} />
				</Dropdown>
			</div>


		</div>
	)
}

export default GridItem
