import { ItemProps } from "../Components/Atoms/GridItem";

import Slip_voet from "../Assets/Images/slip_voet.png";

import doff from "../Assets/Images/doff_n_donner.png";

import dressbuddy from "../Assets/Images/dressbuddy.png";

import gloves from "../Assets/Images/steve_gloves.png";

import butler from "../Assets/Images/butler.png"

import easyslide from "../Assets/Images/easyslide.png"

import knoophaak from "../Assets/Images/knoophaak.png"





// {
