// },

const gridItems: ItemProps[] = [
    {
        text: "Sok aantrekhulp ",
        videoUrl: "https://www.youtube.com/watch?v=8LomDxVuONs",
        imgSrc: doff,
        textExplaination:"Ideaal hulpmiddel voor het aantrekken van sokken en kousen. Alleen geschikt als u met de handen bij de kuit kan komen. De unieke vorm van de flexibele lichtgewicht sokaantrekker houdt de sok op de sokaantrekker tot de sok over de voet getrokken is. Kan in elke gewenste vorm gebogen worden. De lange aantrekbanden kunnen op maat geknipt worden. " 
    },
    {
        text: "Dressbuddy",
        videoUrl: "https://www.youtube.com/watch?v=jfzmtlx3FMQ",
        imgSrc: dressbuddy,
        textExplaination: "Dit pakket bevat de Dressbuddy die geschikt is om therapeutische elastische kousen zonder te bukken zelfstandig aan en uit te trekken. Tevens zijn de Slip Perfect en de Antislipmat toegevoegd om het aan-en uittrekken nog eenvoudiger en sneller te maken. ‘S morgens gebruikt men de gele balletjes (de zon komt op) voor het aantrekken en ’s avonds gebruikt men de blauwe balletjes (zon is onder) uittrekken."
    },
    {
        text: "Knoophaak ",
        videoUrl: "https://youtu.be/cP77l1WNBwo?si=Q4AB__Cr45mQhsi2",
        imgSrc: knoophaak,
        textExplaination:"Het hulpmiddel is geschikt voor mensen met een verminderde handfunctie of slechts een hand ter beschikking hebben. De flexibele rubberen greep met ribbels maken het vasthouden comfortabel voor elke greep. Men steekt de haak door het knoopsgat. Vervolgens gaat men met het brede gedeelte van de haak om de knoop heen en trekt men de knoop naar het smalste gedeelte van de haak. Daarna kan de knopenhaak door het knoopsgat worden getrokken."
    },

    {
        text: "Slip voet",
        videoUrl: "",
        imgSrc: Slip_voet,
        textExplaination: "Slipvoet voor het aan- en uittrekken van therapeutische elastische kousen met open teen en gesloten teen. Dit hulpmiddel kan uitkomst bieden als u nog wel bij uw voeten kunt. "

    },
    {
        text: "Mediven Butler verstelbaar",
        videoUrl: "https://youtu.be/wkF4HotWUG4?si=tueWeHCBUMTeEJXG",
        imgSrc: butler,
        textExplaination: "Deze aantrekhulp voor steunkousen is vooral bedoeld voor kousendragers die zich minder goed kunnen bewegen. U kunt hierbij de hoogte van de handvatten zelf instellen tussen 27 en 59 cm. Zo is dit hulpmiddel altijd geschikt!  "

    },
    {
        text: "Easyslide Magnide",
        videoUrl: "https://www.youtube.com/watch?v=2sWaVV5Sb2I",
        imgSrc: easyslide,
        textExplaination: "De Magnide 2in1 is een hulpmiddel met een innovatieve magneetsluiting voor het aantrekken en uittrekken van  therapeutisch elastische kousen en panty’s met een gesloten teenstuk. "
    },
]

export default gridItems;
